import React, { Component } from 'react';
import NewsItem from './NewsItem';
export default class NewsComponents extends Component {
    constructor(){
        super();
        this.state = {
            articles : [],
            error : "",
            loading : false,
            page : 1
        }
    }


    async componentDidMount(){
        let url = "https://newsapi.org/v2/everything?q=bitcoin&apiKey=a5b29ef6445c49e093e4387d8240d102&page="+this.state.page;
        let parseData,error;
        await fetch(url).then(function(data){
            return data.json();
        }).then(function(res){
            parseData = res.articles;
        }).catch(function(e){
            error = e;       
        })
        this.setState({
            articles : parseData,
            error : (error) ? error : "",
            loading : true
        })
    }
    render() {
        return (
            <>
                <div className="container my-3">
                    <h1 className='mainHeading my-2 mb-4'>NEWSPOINT - LATEST NEWS</h1>
                    <div className="row">
                        {this.state.articles.map(function(elements){
                            let {author,description,publishedAt,source:{id,name},title,url,urlToImage} = elements;
                            return(
                            <div className="col-12 col-lg-4 col-md-6 col-sm-6 col-xs-12 mt-3" key={url}>
                                <NewsItem title={title} description={description} url={url} imageUrl={urlToImage}/>
                            </div>)
                        })}
                    </div>
                </div>
                <div className="container d-flex justify-content-between py-3 pb-5">
                    {(this.state.page>1)?<button className="btn btn-primary">Prev</button>:""}
                    <button className="btn btn-primary me-auto">Next</button>
                </div>
            </>
        )
    }
}
