import React, { Component } from 'react';
import NewsItem from './NewsItem';
export default class NewsComponents extends Component {
    constructor(){
        super();
        this.State = {
            news : "",
            error : "sd",
            loading : false
        }
    }
    componentDidMount(){
        let url = "https://newsapi.org/v2/everything?q=bitcoin&apiKey=a5b29ef6445c49e093e4387d8240d102";
        fetch(url).then(function(data){
            return data.json();
        }).then(function(res){
            this.setState = {
                news : res.articles,
                error : "",
                loading : false
            }
        }).catch(function(e){
            let error = e;       
        })
    }
    render() {
        return (
            <div className="container my-3">
                <h1 className='mainHeading my-2 mb-4'>NEWSPOINT - LATEST NEWS</h1>
                <div className="row">
                    {this.state.articles}
                    <div className="col-12 col-lg-4 col-md-6 col-sm-6 col-xs-12 mt-3">
                        <NewsItem title="News No 01" description="This is a First news to read now" imageUrl="./link"/>
                    </div>
                </div>
            </div>
        )
    }
}
