import React, { Component } from 'react';
import NewsItem from './NewsItem';
export default class NewsComponents extends Component {
    constructor(){
        super();
        this.state = {
            articles : []
        }
    }


    async componentDidMount(){
        let url = "https://newsapi.org/v2/everything?q=bitcoin&apiKey=a5b29ef6445c49e093e4387d8240d102";
        let parseData,error;
        await fetch(url).then(function(data){
            return data.json();
        }).then(function(res){
            parseData = res.articles;
            console.log(parseData)
        }).catch(function(e){
            error = e;       
        })
        this.setState({
            articles : parseData
        })
    }
    render() {
        return (
            <div className="container my-3">
                <h1 className='mainHeading my-2 mb-4'>NEWSPOINT - LATEST NEWS</h1>
                <div className="row">
                    {this.state.articles.map(function(elements){
                        let {author,description,publishedAt,id,name,title,url,urlToImage} = elements;
                        console.log(id+"\n")
                        console.log(name+"\n")
                        console.log(author+"\n")
                        console.log(description+"\n")
                        console.log(publishedAt+"\n")
                        console.log(title+"\n")
                        console.log(url+"\n")
                        console.log(urlToImage+"\n")
                    })}
                    <div className="col-12 col-lg-4 col-md-6 col-sm-6 col-xs-12 mt-3">
                        <NewsItem title="News No 01" description="This is a First news to read now" imageUrl="./link"/>
                    </div>
                </div>
            </div>
        )
    }
}
