import React, { Component } from 'react';
import NewsItem from './NewsItem';
import news from '../news.json';
import Loader from './Loader';
export default class NewsComponents extends Component {
    constructor(){
        super();
        this.articles = news.articles;
        this.state = {
            error : "",
            loading : true,
            page : 1,
            offset : [0,20]
        }
        this.totalPages = (news.articles.length/20)
    }
    async componentDidMount(){
        setTimeout(()=>{
            this.setState({
                loading : false
            })
        },500)
    }
    render() {
        const handleNextPage = async () =>{
            this.setState({
                loading : true,
                offset : [this.state.offset[1],this.state.offset[1]+20]
            })
            window.scrollTo({
                top: 0,
                behavior: "smooth"
            })
            
            setTimeout(()=>{
                this.setState({
                        page : this.state.page+1,
                        loading : false
                })
            },500)
        }
        const handlePrevPage = async ()=>{
            this.setState({
                loading : true,
                offset : [this.state.offset[0]-20,this.state.offset[1]-20]
            })
            window.scrollTo({
                top: 0,
                behavior: "smooth"
            })
            setTimeout(()=>{
                this.setState({
                    page : this.state.page-1,
                    loading : false
                })
            },500)
        }
        return (
            <>
                <div className="container my-3">
                    <h1 className='mainHeading my-2 mb-4'>NEWSPOINT - LATEST NEWS</h1>
                    {(this.state.loading===true)?<Loader/>:""}
                    <div className="row">
                        {!this.state.loading && this.articles.slice(this.state.offset[0],this.state.offset[1]).map(function(elements,key){
                            let {author,description,publishedAt,source:{id,name},title,url,urlToImage} = elements;
                            return(
                            <div className="col-12 col-lg-4 col-md-6 col-sm-6 col-xs-12 mt-3" key={url}>
                                <NewsItem title={title} description={description} url={url} imageUrl={urlToImage}/>
                            </div>)
                        })}
                    </div>
                </div>
                <div className="container d-flex justify-content-center py-3 pb-5">
                    {(this.state.page>1)?this.state.loading===false && <button className="btn btn-primary mx-1" onClick={handlePrevPage}>&#x2190; Prev</button>:""}
                    <div>
                        {
                            {for(){
                                
                            }}
                        }
                        <button className='btn btn-primary mx-1'>{this.totalPages}</button>
                    </div>
                    {(this.state.page===Math.ceil((100/20)))?"":!this.state.loading&&<button className="btn btn-primary mx-1" onClick={handleNextPage}>Next &#8594;</button>}
                </div>
            </>
        )
    }
}
