import React, { Component } from 'react';
import NewsItem from './NewsItem';
import news from '../news.json';
import Loader from './Loader';
export default class NewsComponents extends Component {
    constructor(){
        super();
        this.state = {
            articles : news.articles,
            error : "",
            loading : true,
            page : 1,
            offset : [0,20]
        }
    }
    async componentDidMount(){
        this.setState({
            loading : false
        })
    }
    render() {
        const handleNextPage = async () =>{
            this.setState({
                loading : true,
                offset : [this.state.offset[1],this.state.offset[1]+20]
            })
            window.scrollTo({
                top: 0,
                behavior: "smooth"
            })
            
            setTimeout(()=>{
                
            })
            this.setState({
                    page : this.state.page+1,
                    loading : false
            })
        }
        const handlePrevPage = async ()=>{
            this.setState({
                loading : true
            })
            window.scrollTo({
                top: 0,
                behavior: "smooth"
            })
            let url = `https://newsapi.org/v2/everything?q=bitcoin&apiKey=a5b29ef6445c49e093e4387d8240d102&page=${this.state.page-1}&pageSize=20`;
            let parseData,error;
            await fetch(url).then(function(data){
                return data.json();
            }).then(function(res){
                parseData = res;
            }).catch(function(e){
                error = e;       
            })
            this.setState({
                page : this.state.page-1,
                articles : parseData.articles,
                loading : false
            })
        }
        return (
            <>
                <div className="container my-3">
                    <h1 className='mainHeading my-2 mb-4'>NEWSPOINT - LATEST NEWS</h1>
                    {(this.state.loading===true)?<Loader/>:""}
                    <div className="row">
                        {!this.state.loading && this.state.articles.slice(this.state.offset[0],this.state.offset[1]).map(function(elements,key){
                            let {author,description,publishedAt,source:{id,name},title,url,urlToImage} = elements;
                            return(
                            <div className="col-12 col-lg-4 col-md-6 col-sm-6 col-xs-12 mt-3" key={url}>
                                <NewsItem title={title} description={description} url={url} imageUrl={urlToImage}/>
                            </div>)
                        })}
                    </div>
                </div>
                <div className="container d-flex justify-content-center py-3 pb-5">
                    {(this.state.page>1)?this.state.loading===false && <button className="btn btn-primary mx-1" onClick={handlePrevPage}>&#x2190; Prev</button>:""}
                    {/* <div>
                        <button className='btn btn-primary mx-1'>1</button>
                    </div> */}
                    {(this.state.page===Math.ceil((100/20)))?"":!this.state.loading&&<button className="btn btn-primary mx-1" onClick={handleNextPage}>Next &#8594;</button>}
                </div>
            </>
        )
    }
}
